INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2016-10-03 17:14:52', '2016-10-03 17:14:52', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2016-10-03 17:14:52', '2016-10-03 17:14:52', '', 0, 'http://localhost/shop/?p=1', 0, 'post', '', 1),
(2, 1, '2016-10-03 17:14:52', '2016-10-03 17:14:52', 'This is an example page. It''s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I''m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin'' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href="http://localhost/shop/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2016-10-03 17:14:52', '2016-10-03 17:14:52', '', 0, 'http://localhost/shop/?page_id=2', 0, 'page', '', 0),
(4, 1, '2016-10-03 17:30:18', '2016-10-03 17:30:18', '', 'Shop', '', 'publish', 'closed', 'closed', '', 'shop', '', '', '2016-10-03 17:30:18', '2016-10-03 17:30:18', '', 0, 'http://localhost/shop/index.php/shop/', 0, 'page', '', 0),
(5, 1, '2016-10-03 17:30:18', '2016-10-03 17:30:18', '[woocommerce_cart]', 'Cart', '', 'publish', 'closed', 'closed', '', 'cart', '', '', '2016-10-03 17:30:18', '2016-10-03 17:30:18', '', 0, 'http://localhost/shop/index.php/cart/', 0, 'page', '', 0),
(6, 1, '2016-10-03 17:30:18', '2016-10-03 17:30:18', '[woocommerce_checkout]', 'Checkout', '', 'publish', 'closed', 'closed', '', 'checkout', '', '', '2016-10-03 17:30:18', '2016-10-03 17:30:18', '', 0, 'http://localhost/shop/index.php/checkout/', 0, 'page', '', 0),
(7, 1, '2016-10-03 17:30:18', '2016-10-03 17:30:18', '[woocommerce_my_account]', 'My Account', '', 'publish', 'closed', 'closed', '', 'my-account', '', '', '2016-10-03 17:30:18', '2016-10-03 17:30:18', '', 0, 'http://localhost/shop/index.php/my-account/', 0, 'page', '', 0),
(8, 1, '2016-10-03 17:42:19', '2016-10-03 17:42:19', '', 'Laptop HP Pavillion', '', 'publish', 'open', 'closed', '', 'laptop-hp-pavillion', '', '', '2016-10-03 17:42:19', '2016-10-03 17:42:19', '', 0, 'http://localhost/shop/?post_type=product&#038;p=8', 0, 'product', '', 0);

